function randomRating() {
    let score = pd.rnorm(1, 6, 2)[0];
    if (score > 10) { score = 10; }
    if (score < 0) { score = 0; }
    return Math.floor(score * 100) / 100;
}

function randomReviews(max) {
    let book_reviews = pd.ruf(1)[0];
    book_reviews = max < book_reviews ? max : book_reviews;
    reviews = [ ];
    for (let j = 0; j < book_reviews; j++) {
        reviews.push(txtgen.paragraph());
    }
    return reviews;
}

